// Dentist Scheduler Application
// Written by Brendan Johnston

/*
  This application allows for a Dentist office to schedule, and maintain new appointments. This also allows for customers to create new appointments for themselves.
*/

import logo from './logo.svg';
import './App.css';
import react from 'react';
import NewAppointment from './Components/NewAppointments';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { ListGroup } from 'react-bootstrap';
import axios from 'axios';
import AvailableTimes from './Components/AvailableTimes';


class MainScheduler extends react.Component {
  constructor(props) {
    super(props);

    let date = this.getCurrentDate("/");
    console.log(date);
    this.state = {
      selectedDate: date,
      availableTimes: undefined,
      dateValue: undefined,
      appointmentList: undefined,
    };

    this.checkAvailableTimes();
  }

  onCalendarChange = (param) => {
    console.log(param);
    let date = param.getDate();
    let month = param.getMonth() + 1;
    let year = param.getFullYear();
    let separator = "/";

    let stringVal = `${
      month < 10 ? `0${month}` : `${month}`
    }${separator}${date}${separator}${year}`;

    this.setState({
      selectedDate: stringVal,
    });
    this.checkAvailableTimes();
  };

  dateChange = (event) => {};

  onTimeChange = (change) => {
    this.setState({
      appointmentTime: change,
    });
  };

  checkAvailableTimes = () => {
    var url =
      "http://localhost:5083/DentistScheduler/GetAppointmentsByDate/" +
      this.state.selectedDate;
    const header = {
      "Access-Control-Allow-Origin": "*",
    };
    var success = axios
      .get(url, header)
      .then((availableTimesFromServer) => {
        console.log(availableTimesFromServer);

        let array = [];
        availableTimesFromServer.data.forEach(element => {
          type Appointment = {
            time: element.item1,
            appointmentValue: element.item2
          };
          array.push(Appointment);
        })
        this.setState({ availableTimes: availableTimesFromServer.data });
      })
      .catch((ex) => {
        console.log(ex);
      });
    console.log(success);
  };

  getCurrentDate(separator = "") {
    let newDate = new Date();
    let date = newDate.getDate();
    let month = newDate.getMonth() + 1;
    let year = newDate.getFullYear();

    return `${
      month < 10 ? `0${month}` : `${month}`
    }${separator}${date}${separator}${year}`;
  }

  render() {
    return (
      <div className="App">
        <label className="Header-Label">Smiley Faces Dentist Scheduler</label>
        <Calendar
          onChange={this.onCalendarChange}
          value={this.state.dateValue}
          // tileDisabled={this.tileDisabled}
        />
        <AvailableTimes
          onTimeChange={this.onTimeChange}
          availableTimes={this.state.availableTimes}
          selectedDate={this.state.selectedDate}
        />
        <label>Date Selected: {this.state.selectedDate}</label>

        <NewAppointment date={this.state.selectedDate}></NewAppointment>
      </div>
    );
  }
}



function App() {

  return (
    <div className="App">
      <MainScheduler></MainScheduler>
    </div>
  );
}

export default App;
