// Add in editing appointment and updating on server.
import React from 'react';
import axios from 'axios';
import 'react-phone-number-input/style.css';
import PhoneInput from 'react-phone-number-input';
import 'react-phone-number-input/style.css';
import Modal from 'react-bootstrap/Modal';
import { Dropdown } from 'react-bootstrap';

class AppointmentEdit extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        firstName: "",
        lastName: "",
        phone: "",
        appointmentDate: this.props.date,
        appointmentTime: this.props.time,
      };
    }


    render() {
        return (
          <Modal
            show={this.props.newAppointmentModelIsOpen}
            onHide={this.props.onNewAppointmentClose}
          >
            <Modal.Header closeButton>
              <Modal.Title>New Appointment</Modal.Title>
            </Modal.Header>
            <div >
              <label>Create New Appointment:</label>
              <br />
              <form onSubmit={this.onSubmit}>
                <br />
                <label>First Name:</label>
                <input
                  type="text"
                  name="firstName"
                  required={true}
                  onChange={this.onFirstNameChange}
                />
                <br />
                <label>Last Name:</label>
                <input
                  type="text"
                  name="lastName"
                  required={true}
                  onChange={this.onLastNameChange}
                />
                <br />
                <PhoneInput
                  countries={["US"]}
                  defaultCountry="US"
                  placeholder="Phone Number:"
                  name="phoneNumber"
                  required={true}
                  onChange={this.onPhoneChange}
                ></PhoneInput>
                <Dropdown>
    
                </Dropdown>
                <br />
                <Modal.Footer>
                  <button variant="primary" type="button" value="Submit">
                    Submit
                  </button>
                </Modal.Footer>
              </form>
            </div>
    
            {/* <button onClick={this.goBack}>Return</button> */}
          </Modal>
        );
      }
}  
export default AppointmentEdit;
