// Dentist Scheduler Application
// Written by Brendan Johnston

/*
  This application allows for a Dentist office to schedule, and maintain new appointments. This also allows for customers to create new appointments for themselves.
*/

import logo from "./logo.svg";
import "./App.css";
import react from "react";
import NewAppointment from './Components/NewAppointments';
import AppointmentEdit from './Components/AppointmentEdit';
// import Calendar from "react-calendar";
import {Calendar, momentLocalizer} from 'react-big-calendar';
import moment from 'moment';
// import 'moment/locale/en-US';
//import "react-calendar/dist/Calendar.css";
import axios from "axios";
import AvailableTimes from "./Components/AvailableTimes";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';

moment.locale('en')
const localizer = momentLocalizer(moment);

class MainScheduler extends react.Component {
  constructor(props) {
    super(props);

    let date = this.getCurrentDate("/");
    console.log(date);
    this.state = {
      selectedDate: date,
      availableTimes: undefined,
      dateValue: undefined,
      appointmentList: [],
      newAppointmentIsOpen: false,
      editAppointmentIsOpen: false,
      startTime: "",
      endTime: ""
    };

    this.checkAvailableTimes();
  }

  onCalendarChange = (param) => {
    console.log(param);
    let date = param.getDate();
    let month = param.getMonth() + 1;
    let year = param.getFullYear();
    let separator = "/";

    let stringVal = `${
      month < 10 ? `0${month}` : `${month}`
    }${separator}${date}${separator}${year}`;

    this.setState({
      selectedDate: stringVal,
    });
    this.checkAvailableTimes();
  };

  dateChange = (event) => {};

  onTimeChange = (change) => {
    var start = change.start;
    var end = change.end;
    this.setState({
      startTime: start,
      endTime: end,
      newAppointmentIsOpen: true,
    });
  };

  eventSelected = (change) =>
  {

  }

  checkAvailableTimes = () => {
    var url =
      "http://localhost:5083/DentistScheduler/";
    const header = {
      "Access-Control-Allow-Origin": "*",
    };
    var success = axios
      .get(url, header)
      .then((availableTimesFromServer) => {
        console.log(availableTimesFromServer);

        let array = [];
        availableTimesFromServer.data.forEach(element => {
          var startTime = moment(Number(element.startTime)).toDate();
          var endTime = moment(Number(element.endTime)).toDate();
          var appointment = {
            title: element.firstName + " " + element.lastName + " Phone: " + element.phone,
            phone: element.phone,
            firstName: element.firstName,
            lastName: element.lastName,
            start: startTime,
            end: endTime,
            id: element.id
          };
          array.push(appointment);
        })

        this.setState({appointmentList: array});
        //this.setState({ availableTimes: availableTimesFromServer.data });
      })
      .catch((ex) => {
        console.log(ex);
      });
    console.log(success);
  };

  editAppointmentClose = () =>
  {
    this.setState({
      editappointmentIsOpen: false
    })
    this.checkAvailableTimes();
  }

  newAppointmentClose = () =>
  {
    this.setState({
      newAppointmentIsOpen: false
    });
    this.checkAvailableTimes();
  }

  getCurrentDate(separator = "") {
    let newDate = new Date();
    let date = newDate.getDate();
    let month = newDate.getMonth() + 1;
    let year = newDate.getFullYear();

    return `${
      month < 10 ? `0${month}` : `${month}`
    }${separator}${date}${separator}${year}`;
  }


  render() {
    return (
      <div className="App">
        <header className="Header-Label">Smiley Faces Dentist Scheduler</header>
        <label>A CoFi Special Business Venture!</label>
        <br />
        <label><b>Click</b> on appointment to <b>view and edit</b></label>
        <br />
        <label><b>Click</b> or <b>Click and Drag</b> in empty space to <b>create new appointment</b></label>
        <br />
        <Calendar
          localizer={localizer}
          defaultDate={moment().toDate()}
          events={this.state.appointmentList}
          startAccessor="start"
          endAccessor="end"
          views={['month', 'week', 'day']}
          defaultView='week'
          selectable={true}
          onSelectSlot={this.onTimeChange}
          onSelectEvent={this.eventSelected}
          scrollToTime={moment().set({h: 8, m: 0}).toDate()}
          style={{ height: 1080 }}
        />
        <AvailableTimes
          style={{ display: "flex" }}
          onTimeChange={this.onTimeChange}
          availableTimes={this.state.availableTimes}
          selectedDate={this.state.selectedDate}
        />
        <br />
        <AppointmentEdit
        startTime={this.state.selectedStartTime}
        endTime={this.state.selectedEndTime}
        phone={this.state.selectedPhone}
        firstName={this.state.selectedFirstName}
        lastName={this.state.selectedLastName}
        id={this.state.selectedid}I
        editAppointmentModelIsOpen={this.state.newAppointmentIsOpen}
        onEditAppointmentClose={this.editAppointmentClose}
        >

        </AppointmentEdit>
        <NewAppointment
          startTime={this.state.startTime}
          endTime={this.state.endTime}
          newAppointmentModelIsOpen={this.state.newAppointmentIsOpen}
          onNewAppointmentClose={this.newAppointmentClose}
        ></NewAppointment>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App" style={{display: "table", width: "100%"}}>
      <header className="App-Header">
        <MainScheduler></MainScheduler>
      </header>
    </div>
  );
}

export default App;
