import React from 'react';
import axios from 'axios';
import 'react-phone-number-input/style.css'
import PhoneInput from 'react-phone-number-input'
import 'react-phone-number-input/style.css'

class NewAppointment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      phone: "",
      appointmentDate: this.props.date,
      appointmentTime: this.props.time,
    };
  }

  onSubmit = (submission) => {
    // let formData = new FormData(); //formdata object
    // formData.append("name", "ABC"); //append the values with key, value pair
    // formData.append("age", 20);

    if (this.state.firstName === undefined || this.state.lastName === undefined || this.state.phone === undefined)
    {
      alert("Missing Form Information, please try again");
      return;
    }
    else if (this.state.appointmentDate === undefined || this.state.appointmentTime === undefined)
    {
      alert("Please select a Date and Time");
    }
    var formData = new FormData();
    formData.append("firstName", this.state.firstName);
    formData.append("lastName", this.state.lastName);
    formData.append("phone", this.state.phone);
    formData.append("date", this.state.appointmentDate);
    formData.append("time", this.state.appointmentTime);
    var url =
    "http://localhost:5083/DentistScheduler/CreateNewAppointment";
    const config = {
      headers: { "content-type": "multipart/form-data" },
    };


    axios
      .post(url, formData, config)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
    //XMLHttpRequest.send() method as multipart form data

    
  };

  onFirstNameChange = (change) => {

    this.setState({
      firstName: change.target.value
    })
  };

  onLastNameChange = (change) => {
    this.setState({
      lastName: change.target.value
    })
  };

  // Value is in E.164 format ex: +12133734253
  onPhoneChange = (change) => {
    this.setState({
      phone: change
    })
    console.log(change);
  };

  render() {
    return (
      <div>
        <label>Create New Appointment:</label>
        <br />
        <label>Date Selected: {this.state.appointmentDate}</label>
        <form onSubmit={this.onSubmit}>
          <nbst />
          <label>First Name:</label>
          <input
            type="text"
            name="firstName"
            required={true}
            onChange={this.onFirstNameChange}
          />
          <label>Last Name:</label>
          <input
            type="text"
            name="lastName"
            required={true}
            onChange={this.onLastNameChange}
          />
          <br />
          <PhoneInput
            countries={["US"]}
            defaultCountry="US"
            placeholder="Phone Number:"
            name="phoneNumber"
            required={true}
            onChange={this.onPhoneChange}
          ></PhoneInput>
          <br />
          <button type="button" value="Submit">
            Submit New Appointment
          </button>
        </form>
        <button onClick={this.goBack}>Return</button>
      </div>
    );
  }
}

export default NewAppointment;
