// Add in editing appointment and updating on server.
import React from "react";
import axios from "axios";
import "react-phone-number-input/style.css";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
import Modal from "react-bootstrap/Modal";
import { Dropdown, Button } from "react-bootstrap";
import moment from "moment";

class AppointmentEdit extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: this.props.firstName,
      lastName: this.props.lastName,
      phone: this.props.phone,
      startTime: this.props.startTime,
      endTime: this.props.endTime,
    };
  }

  changeButtonClick = (submission) => {
    // let formData = new FormData(); //formdata object
    // formData.append("name", "ABC"); //append the values with key, value pair
    // formData.append("age", 20);

    if (
      this.state.firstName === undefined ||
      this.state.lastName === undefined ||
      this.state.phone === undefined
    ) {
      alert("Missing Form Information, please try again");
      return;
    } else if (
      this.state.appointmentDate === undefined ||
      this.state.appointmentTime === undefined
    ) {
      alert("Please select a Date and Time");
    }

    var startTimeUnix = moment(this.props.startTime).format("x");
    var endTimeUnix = moment(this.props.endTime).format("x");
    var newAppointmentFormData = new FormData();
    newAppointmentFormData.append("FirstName", this.state.firstName);
    newAppointmentFormData.append("LastName", this.state.lastName);
    newAppointmentFormData.append("Phone", this.state.phone);
    newAppointmentFormData.append("StartTime", startTimeUnix);
    newAppointmentFormData.append("EndTime", endTimeUnix);
    var updateAppointmentUrl =
      "http://localhost:5083/DentistScheduler/UpdateAppointment";
    const config = {
      headers: { "content-type": "multipart/form-data" },
    };

    var newAppointmentTimes = new FormData();
    newAppointmentTimes.append("StartTime", startTimeUnix);
    newAppointmentTimes.append("EndTime", endTimeUnix);
    const newAppointmentTimesUrl =
      "http://localhost:5083/DentistScheduler/CheckExistingAppointmentInTime";

    // Check for existing appointments and warn user.
    axios
      .post(newAppointmentTimesUrl, newAppointmentTimes, config)
      .then((response) => {
        if (response.status === 200) {
          alert(
            "Warning: Appointment created at this time already.\n\nClick on appointment to remove."
          );
        }
        // Update Appointment.
        axios
          .post(updateAppointmentUrl, newAppointmentFormData, config)
          .then((response) => {
            console.log(response);
            alert("Successful Update of Appointment!");
            this.props.onEditAppointmentClose();
          })
          .catch((error) => {
            console.log(error);
            alert(
              "Appointment Creation Failed - StatusCode: " +
                error.response.status +
                "\n" +
                error.response.data
            );
          });
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  deleteButtonClick = (event) => {
    var deleteFormData = new FormData();
    deleteFormData.append("id", this.state.id);
    const url = "http://localhost:5083/DentistScheduler/DeleteAppointment";
    const config = {
      headers: { "content-type": "multipart/form-data" },
    };

    // Update Appointment.
    axios
      .delete(url, this.state.id, config)
      .then((response) => {
        console.log(response);
        alert("Successful Deletion of Appointment!");
        this.props.onEditAppointmentClose();
      })
      .catch((error) => {
        console.log(error);
        alert(
          "Appointment Creation Failed - StatusCode: " +
            error.response.status +
            "\n" +
            error.response.data
        );
      });
  };

  onFirstNameChange = (change) => {
    this.setState({
      firstName: change.target.value,
    });
  };

  onLastNameChange = (change) => {
    this.setState({
      lastName: change.target.value,
    });
  };

  // Value is in E.164 format ex: +12133734253
  onPhoneChange = (change) => {
    this.setState({
      phone: change,
    });
    console.log(change);
  };

  render() {
    return (
      <Modal
        show={this.props.editAppointmentIsOpen}
        onHide={this.props.onEditAppointmentClose}
      >
        <Modal.Header closeButton>
          <Modal.Title>Edit Appointment</Modal.Title>
        </Modal.Header>
        <div style={{ paddingLeft: "3px" }}>
          <label>Please delete appointment to change time.</label>
          <form>
            <br />
            <label>First Name:</label>
            <input
              type="text"
              name="firstName"
              value={this.state.firstName}
              required={true}
              onChange={this.onFirstNameChange}
            />
            <br />
            <label>Last Name:</label>
            <input
              type="text"
              name="lastName"
              value={this.state.lastName}
              required={true}
              onChange={this.onLastNameChange}
            />
            <br />
            <PhoneInput
              countries={["US"]}
              defaultCountry="US"
              placeholder="Phone Number:"
              value={this.state.phone}
              name="phoneNumber"
              required={true}
              onChange={this.onPhoneChange}
            ></PhoneInput>
            <Dropdown></Dropdown>
            <br />
            <Modal.Footer>
              <Button
                variant="danger"
                type="button"
                value="Delete"
                onClick={this.deleteButtonClick}
              >
                Delete
              </Button>
              <span style={{ paddingLeft: "15px" }}></span>
              <Button
                variant="primary"
                type="button"
                value="Submit"
                onClick={this.changeButtonClick}
              >
                Update
              </Button>
            </Modal.Footer>
          </form>
        </div>
      </Modal>
    );
  }
}
export default AppointmentEdit;
