import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import axios from 'axios';

const disabledDates = [Calendar.tomorrow, Calendar.in3Days, Calendar.in5Days];

function tileDisabled({ date, view }) {
  // Disable tiles in month view only
  if (view === 'month') {
    // Check if a date React-Calendar wants to check is on the list of disabled dates
    return disabledDates.find(dDate => Calendar.isSameDay(dDate, date));
  }
}

function MyApp() {
  const [value, setValue] = useState(new Date());
  const date = undefined;
  return (
    <Calendar
      onChange={onChange}
      value={date}
      tileDisabled={tileDisabled}
    />
  );
}

function onChange()
{
  
  axios.post()
}


class ScheduleCalendar extends React.Component {
  constructor(props) {
    super(props);
    {
        MyApp();
      this.state = {
        value: "",
        selectedDate: undefined,
        disabledDates: [Calendar.tomorrow, Calendar.in3Days, Calendar.in5Days],
      };
    }
  }

//   tileDisabled = ({ date, view }) => {

//     // Check if a date React-Calendar wants to check is on the list of disabled dates
//     returnVal = this.state.disabledDates.find((dDate) => Calendar.isSameDay(dDate, date));
//     return returnVal;
    
//   };

//   onChange = (param) => {
//     this.setState({
//       selectedDate: param,
//     });
//   };

  render() {
    return (
      <div className="center">
        <Calendar
          onChange={this.onChange}
          value={this.state.value}
          tileDisabled={this.tileDisabled}
        />
      </div>
    );
  }
}

export default ScheduleCalendar;
