// Add in editing appointment and updating on server.
import React from "react";
import axios from "axios";
import "react-phone-number-input/style.css";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
import Modal from "react-bootstrap/Modal";
import { Dropdown, Button } from "react-bootstrap";
import moment from "moment";

class AppointmentEdit extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      newFirstName: "",
      newLastName: "",
      newPhone: "",
    };
  }

  changeButtonClick = (submission) => {
    // let formData = new FormData(); //formdata object
    // formData.append("name", "ABC"); //append the values with key, value pair
    // formData.append("age", 20);

    if (
      this.state.firstName === undefined ||
      this.state.lastName === undefined ||
      this.state.phone === undefined
    ) {
      alert("Missing Form Information, please try again");
      return;
    } else if (
      this.state.appointmentDate === undefined ||
      this.state.appointmentTime === undefined
    ) {
      alert("Please select a Date and Time");
    }

    // var startTimeUnix = moment(this.props.startTime).format("x");
    // var endTimeUnix = moment(this.props.endTime).format("x");

    var newAppointmentFormData = new FormData();
    if (this.state.newFirstName !== "")
    {
        newAppointmentFormData.append("FirstName", this.state.newFirstName);
    }
    else{
        newAppointmentFormData.append("FirstName", this.props.firstName);
    }
    if (this.state.newLastName !== "")
    {
        newAppointmentFormData.append("LastName", this.state.lastName);
    }
    else{
        newAppointmentFormData.append("Phone", this.props.phone);
    }
    if (this.state.newPhone !== "")
    {
        newAppointmentFormData.append("Phone", this.state.phone);
    }
    else{
        newAppointmentFormData.append("Phone", this.props.phone);
    }
    newAppointmentFormData.append("StartTime", this.props.startTime);
    newAppointmentFormData.append("EndTime", this.props.endTime);
    var updateAppointmentUrl =
      "http://localhost:5083/DentistScheduler/" + this.props.id;
    const config = {
      headers: { "content-type": "multipart/form-data" },
    };

    // Update Appointment.
    axios
      .post(updateAppointmentUrl, newAppointmentFormData, config)
      .then((response) => {
        console.log(response);
        alert("Successful Update of Appointment!");
        this.props.onEditAppointmentClose();
      })
      .catch((error) => {
        console.log(error);
        alert(
          "Appointment Creation Failed - StatusCode: " +
            error.response.status +
            "\n" +
            error.response.data
        );
      });
  };

  deleteButtonClick = (event) => {
    const url = "http://localhost:5083/DentistScheduler/" + this.props.id;
    // const config = {
    //   headers: { "content-type": "multipart/form-data" },
    // };

    // Delete Appointment
    axios
      .delete(url)
      .then((response) => {
        console.log(response);
        alert("Successful Deletion of Appointment!");
        this.props.onEditAppointmentClose();
      })
      .catch((error) => {
        console.log(error);
        alert(
          "Appointment Creation Failed - StatusCode: " +
            error.response.status +
            "\n" +
            error.response.data
        );
      });
  };

  onFirstNameChange = (change) => {
    this.setState({
      newFirstName: change.target.value,
    });
  };

  onLastNameChange = (change) => {
    this.setState({
      newLastName: change.target.value,
    });
  };

  // Value is in E.164 format ex: +12133734253
  onPhoneChange = (change) => {
    this.setState({
      newPhone: change,
    });
    console.log(change);
  };

  render() {
    return (
      <Modal
        show={this.props.editAppointmentIsOpen}
        onHide={this.props.onEditAppointmentClose}
      >
        <Modal.Header closeButton>
          <Modal.Title>Edit Appointment</Modal.Title>
        </Modal.Header>
        <div style={{ paddingLeft: "3px" }}>
          <label>Please delete appointment to change time.</label>
          <br />
          <label>Enter new values you wish to change</label>
          <form>
            <br />
            <label>Current Values:</label><br /> 
            <label>First Name: {this.props.firstName}</label><br />
            <label>Last Name: {this.props.lastName}</label><br />
            <label>Number: {this.props.phone}</label><br />

            <label>New: </label>
            <input
              type="text"
              name="firstName"
              required={true}
              onChange={this.onFirstNameChange}
            />
            <br />
            <span></span>
            <input
              type="text"
              name="lastName"
              required={true}
              onChange={this.onLastNameChange}
            />
            <br />
            <PhoneInput
              countries={["US"]}
              defaultCountry="US"
              placeholder="Phone Number:"
              name="phoneNumber"
              required={true}
              onChange={this.onPhoneChange}
            ></PhoneInput>
            <Dropdown></Dropdown>
            <br />
            <Modal.Footer>
              <Button
                variant="danger"
                type="button"
                value="Delete"
                onClick={this.deleteButtonClick}
              >
                Delete
              </Button>
              <span style={{ paddingLeft: "15px" }}></span>
              <Button
                variant="primary"
                type="button"
                value="Submit"
                onClick={this.changeButtonClick}
              >
                Update
              </Button>
            </Modal.Footer>
          </form>
        </div>
      </Modal>
    );
  }
}
export default AppointmentEdit;
