// Dentist Scheduler Application
// Written by Brendan Johnston

/*
  This application allows for a Dentist office to schedule, and maintain new appointments. This also allows for customers to create new appointments for themselves.
*/

import logo from './logo.svg';
import './App.css';
import react from 'react';
import NewAppointment from './Components/NewAppointments';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';


class MainScheduler extends react.Component {
  constructor(props) {
    super(props);

    let date = this.getCurrentDate('-');
    console.log(date);
    this.state = {
      selectedDate: date,
    };
  }

  onCalendarChange = (param) => {
    console.log(param);
    this.setState({
      selectedDate: param.getDate(),
      dateValue: undefined
    });
  };

  dateChange = (event) => {};

  getCurrentDate(separator = "") {
    let newDate = new Date();
    let date = newDate.getDate();
    let month = newDate.getMonth() + 1;
    let year = newDate.getFullYear();

    return `${year}${separator}${
      month < 10 ? `0${month}` : `${month}`
    }${separator}${date}`;
  }

  render() {
    return (
      <div className="App">
        <label className="Header-Label">Smiley Faces Dentist Scheduler</label>
        <Calendar
          onChange={this.onCalendarChange}

          // value={this.state.value}
          // tileDisabled={this.tileDisabled}
        />

        <NewAppointment></NewAppointment>
      </div>
    );
  }
}



function App() {

  return (
    <div className="App">
      <MainScheduler></MainScheduler>
    </div>
  );
}

export default App;
