import React from 'react';
import axios from 'axios';
import 'react-phone-number-input/style.css';
import PhoneInput from 'react-phone-number-input';
import 'react-phone-number-input/style.css';
import Modal from 'react-bootstrap/Modal';
import { Dropdown } from 'react-bootstrap';
import moment from 'moment';


class NewAppointment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      phone: "",
    };
  }

  onSubmit = (submission) => {
    // let formData = new FormData(); //formdata object
    // formData.append("name", "ABC"); //append the values with key, value pair
    // formData.append("age", 20);

    if (
      this.state.firstName === undefined ||
      this.state.lastName === undefined ||
      this.state.phone === undefined
    ) {
      alert("Missing Form Information, please try again");
      return;
    } else if (
      this.props.startTime === undefined ||
      this.props.endTime === undefined
    ) {
      alert("Please select a Date and Time");
      return;
    }
    var startTimeUnix = moment(this.props.startTime).format("x");
    var endTimeUnix = moment(this.props.endTime).format("x");
    //var endTimeUnix = moment().unix(this.props.endTime);

    var newAppointmentFormData = new FormData();
    newAppointmentFormData.append("FirstName", this.state.firstName);
    newAppointmentFormData.append("LastName", this.state.lastName);
    newAppointmentFormData.append("Phone", this.state.phone);
    newAppointmentFormData.append("StartTime", startTimeUnix);
    newAppointmentFormData.append("EndTime", endTimeUnix);
    const newAppointmentUrl =
      "http://localhost:5083/DentistScheduler/CreateNewAppointment";
    const config = {
      headers: { "content-type": "multipart/form-data" },
    };

    var newAppointmentTimes = new FormData();
    newAppointmentTimes.append("StartTime", startTimeUnix);
    newAppointmentTimes.append("EndTime", endTimeUnix);
    const newAppointmentTimesUrl =
      "http://localhost:5083/DentistScheduler/CheckExistingAppointmentInTime";

    // Check for existing appointments and warn user.
    axios
      .post(newAppointmentTimesUrl, newAppointmentTimes, config)
      .then((response) => {
        if (response.status === 200) {
          alert("Warning: Appointment created at this time already.\n\nClick on appointment to remove.");
        }
        // Create new appointment.
        axios
          .post(newAppointmentUrl, newAppointmentFormData, config)
          .then((response) => {
            console.log(response);
            alert("Successful Creation of Appointment!");
            this.props.onNewAppointmentClose();
          })
          .catch((error) => {
            console.log(error);
            alert(
              "Appointment Creation Failed - StatusCode: " +
                error.response.status +
                "\n" +
                error.response.data
            );
          });
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });

    //XMLHttpRequest.send() method as multipart form data
  };

  onFirstNameChange = (change) => {
    this.setState({
      firstName: change.target.value,
    });
  };

  onLastNameChange = (change) => {
    this.setState({
      lastName: change.target.value,
    });
  };

  // Value is in E.164 format ex: +12133734253
  onPhoneChange = (change) => {
    this.setState({
      phone: change,
    });
    console.log(change);
  };

  render() {
    return (
      <Modal
        show={this.props.newAppointmentModelIsOpen}
        onHide={this.props.onNewAppointmentClose}
      >
        <Modal.Header closeButton>
          <Modal.Title>New Appointment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div style={{ paddingLeft: "3px" }}>
            <label>Create New Appointment:</label>
            <br />
            {/* <label>Date: {this.props.startTime.getMonth()}/{this.props.startTime.getDay()}/{this.props.startTime.getYear()}</label> */}
            <label>
              Date: {moment(this.props.startTime).format("MM/DD/YYYY")}
            </label>
            <br />
            <label>
              Start Time: {moment(this.props.startTime).format("hh:mm A")}
            </label>
            <br />
            <label>
              End Time: {moment(this.props.endTime).format("hh:mm A")}
            </label>
            <br />
            <form onSubmit={this.onSubmit} action="#">
              <br />
              <label>First Name:</label>
              <span>&nbsp;</span>
              <input
                type="text"
                name="firstName"
                required={true}
                onChange={this.onFirstNameChange}
              />
              <br />
              <label>Last Name:</label>
              <span>&nbsp;</span>
              <input
                type="text"
                name="lastName"
                required={true}
                onChange={this.onLastNameChange}
              />
              <br />
              <PhoneInput
                countries={["US"]}
                defaultCountry="US"
                placeholder="Phone Number:"
                name="phoneNumber"
                required={true}
                onChange={this.onPhoneChange}
              ></PhoneInput>
              <br />
              <Modal.Footer>
                <button type="submit" variant="primary" value="Submit">
                  Submit
                </button>
              </Modal.Footer>
            </form>
          </div>
        </Modal.Body>
      </Modal>
    );
  }
}

export default NewAppointment;
